import React from 'react'
import Drawer from "@material-ui/core/Drawer";
import List from "@material-ui/core/List";
import ListItem from "@material-ui/core/ListItem";
import {scroller} from 'react-scroll'


const sideDrawer = (props) => {

    const scrollToElem = (elem) => {
        scroller.scrollTo(elem, {
            duration: 1500,
            delay: 100,
            smooth: true
        })
    }

    return (
        <Drawer
            anchor='right'
            open={props.open}
            onClose={() => props.onClose(false)}

        >
            <List component='nav'>
                <ListItem button onClick={() => scrollToElem('Event starts in')}>Event starts in</ListItem>
                <ListItem button onClick={() => scrollToElem('Avatar 2 INFO')}>Avatar 2 INFO</ListItem>
                <ListItem button onClick={() => scrollToElem('Highlights')}>Highlights</ListItem>
                <ListItem button onClick={() => scrollToElem('Pricing')}>Pricing</ListItem>
                <ListItem button onClick={() => scrollToElem('Location')}>Location</ListItem>
            </List>
        </Drawer>
    )
}

export default sideDrawer
