import React from 'react'
import {Button} from '@material-ui/core'
import TicketsIcon from 'images/icons/ticket.png'

const MyButton = (props) => (
    <Button
        href= {props.path || 'http://google.com'}
        size= 'small'
        variant='contained'
        style={{
            background: props.bgc || 'red',
            color: props.cl || 'white'
        }}
    >
        <img
            src={TicketsIcon}
            alt="ticket"
            className='iconImage'
        />
        {props.name || 'Button'}
    </Button>
)

export default MyButton
